function setLocalStorage(key,value,ttl){const now=new Date,item={value:value,expiry:now.getTime()+ttl};localStorage.setItem(key,JSON.stringify(item))}function getLocalStorage(key){const itemStr=localStorage.getItem(key);if(!itemStr)return null;const item=JSON.parse(itemStr),now=new Date;return now.getTime()>item.expiry?(localStorage.removeItem(key),null):item.value}